			scanf("%s", arrStuNum);

			pTemp = FindStuByNum(arrStuNum);

			//打印
			if (NULL != pTemp) {
				printf("学号:%s, 姓名:%s, 分数:%d\n", pTemp->arrStuNum, pTemp->arrStuName, pTemp->iStuScore);
			}
			break;
		case 3:
			//查找指定学生的信息
			printf("输入指定学号\n");
			scanf("%s", arrStuNum);

			//查找
			pTemp = FindStuByNum(arrStuNum);

			//打印
			if (NULL != pTemp) {
				//修改学号
				printf("请输入学号\n");
				scanf("%s", arrStuNum);
				strcpy(pTemp->arrStuNum, arrStuNum);

				//修改名字
				printf("请输入姓名\n");
				scanf("%s", arrStuName);
				strcpy(pTemp->arrStuName, arrStuName);

				//修改分数
				printf("请输入分数\n");
				scanf("%d", &iStuScore);
				pTemp->iStuScore = iStuScore;
			}

			break;
		case 4:
			//保存学生信息
			SaveStuToFile();
			break;
		case 6:
			//删除一个节点 
			printf("输入要删除的学生的学号\n");
			scanf("%s", arrStuNum);

			//查找
			pTemp = FindStuByNum(arrStuNum);

			//删除一个节点
			if (NULL != pTemp) {
				DeleteStuNode(pTemp);
			}

			break;
		case 7:
			//恢复
			FreeLinkData();
			g_pHead = NULL;
			g_pEnd = NULL;
			ReadStuFromFile();
			break;
		case 9:
			ShowStuData();
			break;
		case 10:
			//查看指令
			ShowOrder();
			break;
		case 0:
			nFlag = 0;
			break;
		default:
			printf("not right\n");
		}
	}

	//保存学生信息
	SaveStuToFile();

	//释放链表
	FreeLinkData();
	return 0;
}

//添加一个学生信息
void AddStuMSG(char arrStuNum[10], char arrStuName[10], int iStuScore)
{
	//逻辑
	//创建一个节点
	STUNODE* pTemp = (STUNODE*)malloc (sizeof (STUNODE));
	//检验参数的合法性
	if (NULL == arrStuNum || NULL == arrStuName || iStuScore < 0) {
		printf("学生信息输入错误！\n");
		return;
	}

	//赋值
	strcpy(pTemp->arrStuNum, arrStuNum);
	strcpy(pTemp->arrStuName, arrStuName);
	pTemp->iStuScore = iStuScore;
	pTemp->pNext = NULL;

	//接在链表上
	if (NULL == g_pHead || NULL == g_pEnd) {
		g_pHead = pTemp;
	} else {
		g_pEnd->pNext = pTemp;
	}
	g_pEnd = pTemp;

} 

void FreeLinkData()
{
	STUNODE* pTemp = g_pHead;
	while (g_pHead != NULL) {
		//记录结点
		pTemp = g_pHead;
		//向后移动一个
		g_pHead = g_pHead->pNext; 
		//删除节点
		free(pTemp);
	}
}

//打印数据
void ShowStuData()
{
	STUNODE* pTemp = g_pHead;
	while (NULL != pTemp)
	{
		printf("学号:%s, 姓名:%s, 分数:%d\n", pTemp->arrStuNum, pTemp->arrStuName, pTemp->iStuScore);
		//向下走一步
		pTemp = pTemp->pNext;
	}
}

//显示指令
void ShowOrder()
{
	printf("***********学生信息管理系统************************\n");
	printf("***********本系统操作指令如下**********************\n");
	printf("***	  1、增加一个学生信息（尾添加）		***\n");
	printf("***	 11、增加一个学生信息（头添加）		***\n");
	printf("***	111、增加一个学生信息（在指定位置添加） ***\n");
	printf("***	  2、查找指定学生的信息（姓名/学号） 	***\n");
	printf("***	  3、修改指定学生的信息			***\n");
	printf("***	  4、保存业主的信息到文件中		***\n");
	printf("***	  5、读取文件的业主信息			***\n");
	printf("***	  6、删除指定学生的信息			***\n");
	printf("***	  7、恢复删除的学生的信息		***\n");
	printf("***	  9、显示所有学生的信息			***\n");
	printf("***	  0、退出系统				***\n");
}

void AddStuMSGToLinkHead(char arrStuNum[10], char arrStuName[10], int iStuScore)
{
	//创建一个节点
	STUNODE* pTemp = (STUNODE*)malloc(sizeof(STUNODE));
	//检测参数合法性
	if (NULL == arrStuNum || NULL == arrStuName || iStuScore < 0)
	{
		printf("学生信息输入错误！\n");
		return;
	}

	strcpy(pTemp->arrStuNum, arrStuNum);
	strcpy(pTemp->arrStuName, arrStuName);
	pTemp->iStuScore = iStuScore;
	pTemp->pNext = NULL;

	//链表为空
	if (NULL == g_pHead || NULL == g_pEnd) {
		g_pEnd = pTemp;
	} else {
		pTemp->pNext = g_pHead;
	}

	g_pHead = pTemp;

}

//查找制定学生
STUNODE* FindStuByNum(char* arrStuNum)
{
	STUNODE* pTemp = g_pHead;
	//判断参数的合法性
	if (NULL == arrStuNum) {
		printf("学号输入错误！\n");
		return NULL;
	}

	//判断链表是否为空
	if (NULL == g_pHead || NULL ==g_pEnd) {
		printf("链表为NULL！\n");
		return NULL;
	}

	//遍历链表
	while (NULL != pTemp) {
		if (0 == strcmp(pTemp->arrStuNum, arrStuNum)) {
			return pTemp;
		}

		pTemp = pTemp->pNext;
	}
	printf("查无此节点！\n");
	return NULL;
}

//指定位置插入节点
void InsertNode(STUNODE* pTemp, char arrStuNum[10], char arrStuName[10], int iStuScore)
{
	//创建节点
	STUNODE* pNewTemp = (STUNODE*)malloc(sizeof(STUNODE));

	//成员赋值
	strcpy(pNewTemp->arrStuNum, arrStuNum);
	strcpy(pNewTemp->arrStuName, arrStuName);
	pNewTemp->iStuScore = iStuScore;
	pNewTemp->pNext = NULL;

	//判断插入位置是否为未结点
	if (pTemp == g_pEnd) {
		g_pEnd->pNext = pNewTemp;
		g_pEnd = pNewTemp;
	} else {
		pNewTemp->pNext =  pTemp->pNext;
		pTemp->pNext =  pNewTemp;
	}
}

//删除指定的学生
void DeleteStuNode(STUNODE* pNode)
{
	//只有一个节点
	if (g_pHead == g_pEnd) {
		free(g_pHead);
		g_pHead = NULL;
		g_pEnd = NULL;
		return;
	}

	//如果是首节点
	if (g_pHead == pNode) {
		free(g_pHead);
		g_pHead = g_pHead->pNext;
		return;
	}
	//如果是尾节点
	STUNODE* pTemp = g_pHead;
	if (g_pEnd == pNode) {
		while (pTemp->pNext != pNode) {
			pTemp = pTemp->pNext;
		}
		pTemp->pNext = NULL;
		g_pEnd = pTemp;
		free(pNode);
		return;
	}

	//如果是中间节点
	if (pNode != g_pHead && pNode != g_pEnd && g_pHead != g_pEnd) {
		while (pTemp->pNext != pNode) {
			pTemp = pTemp->pNext;
		}
		pTemp->pNext = pNode->pNext;
		free(pNode);
		return;

	}
}

//保存信息到文件中
void SaveStuToFile()
{
	FILE* pFile = NULL;
	STUNODE* pTemp = g_pHead;
	char strBuf[30] = {0};
	char strScore[10] = {0};

	//判断链表中是否为null
	if (g_pHead == NULL) {
		printf("没有学生\n");
		return;
	}

	//打开文件
	pFile = fopen("dat.dat", "wb+");
	if (NULL == pFile) {
		printf("文件打开失败！\n");
		return;
	}
	//操作文件指针
	while (NULL != pTemp) {
		//学号赋值进去
		strcpy(strBuf, pTemp->arrStuNum);
		strcat(strBuf, ".");
		//姓名
		strcat(strBuf, pTemp->arrStuName);
		strcat(strBuf, ".");
		//分数
		snprintf(strScore, 10, "%d", pTemp->iStuScore);  //linux下不能使用itoa函数，故用此代替
		//拼接字符串
		strcat(strBuf, strScore);

		fwrite(strBuf, 1, strlen(strBuf), pFile);
		fwrite("\r\n", 1, strlen("\r\n"), pFile);

		pTemp = pTemp->pNext;
	}
	//关闭文件
	fclose(pFile);
}

//读取文件中的学生信息
void ReadStuFromFile()
{
	FILE* pFile = fopen("dat.dat", "rb+");
	char strBuf[30] = {0};
	char strStuNum[10] = {0};
	char strStuName[10] = {0};
	char strStuScore[10] = {0};
	int nCount = 0;
	int iStuScore;


	if (NULL == pFile) {
		printf("文件打开失败\n");
		return;
	}

	//操作指针读取函数

	while (NULL != fgets(strBuf, 30, pFile)) {
		int i = 0;
		int j = 0;
		int nCount = 0;
		for (i = 0; strBuf[i] != '\r'; i++) {
			//没到.
			if (0 == nCount) {
				strStuNum[i] = strBuf[i];
				if ('.' == strBuf[i]) {
					strStuNum[i] = '\0';
					nCount++;
				}
			} else if (1 == nCount) {
				if ('.' != strBuf[i]) {
					strStuName[j] = strBuf[i];
					j++;
				}
				if ('.' == strBuf[i]) {
					strStuName[j] = '\0';
					nCount++;
					j = 0;
				}

			} else {
				strStuScore[j] = strBuf[i];
				j++;
			}
		}
		//将学生的分数转化为int类型
		iStuScore = strtol(strStuScore, NULL, 10);
		AddStuMSG(strStuNum, strStuName, iStuScore);
	}
	fclose(pFile);
}

```

